        List<Long> lineOrderIds = new ArrayList<>();
        List<Account> account = message.getAccount();
        account.forEach(a -> {
          if (a.getLineOrders() != null && a.getLineOrders().size() > 0) {
            lineOrderIds.addAll(a.getLineOrders());
          } else {
            accountIds.add(a.getId());
          }
