        Calendar calendar = Calendar.getInstance(); // calendar.set(2019, 11, 26);
        Date date = calendar.getTime();//DateUtils.addDays(, -1);//DateUtils.addMonths( -1);
        uploadFrequencyFile(date, accountIds, lineOrderIds);
        break;
      default:
        // 
        break;
    }
  }


}


